import React from 'react';
import Button from '@mui/material/Button';

function ChoicesArea({ choices, setChoices, selectedLine, setSelectedLine }) {
  return (

       <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', marginLeft: '6%', marginRight: '20%', marginTop: '20px' }}>
      <label style={{ flex: 1 }}>Choices</label>
      <div style={{ flex: 2, border: '1px solid #ccc', borderRadius: '4px', padding: '10px', marginRight: '30px', marginLeft: '68px', marginTop: '20px' }}> 
        {choices.map((each, index) => (
          <div 
            key={index}
            style={{ 
              padding: '5px', 
              cursor: 'pointer', 
              backgroundColor: selectedLine === index ? '#f0f0f0' : 'transparent' // 如果是选中的行，设置背景颜色
            }}
            onClick={() => setSelectedLine(index)}
          >
            {each.value}
          </div>
        ))}
      </div>
      <Button 

        variant="outlined" 
        type="button"
        onClick={() => {
          if (selectedLine !== null) {
            const newChoices = [...choices];
            newChoices.splice(selectedLine, 1);
            setChoices(newChoices);
            setSelectedLine(null);
          }
        }}>
        Delete
      </Button>
    </div>
  );
}

export default ChoicesArea;
