import React from 'react';
import Button from '@mui/material/Button';

function ChoicesArea({ choices, setChoices, selectedLine, setSelectedLine }) {
  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'flex-start' }}>
      <div style={{ display: 'flex', alignItems: 'center' }}>
        <span style={{ flex: 1, textAlign: 'left' }}>Choices</span>
        <textarea
          style={{ flex: 2, marginLeft: '85px', width: '100%' }} // Adjusted style here
          value={choices.map((each) => each.value).join("\n")}
          rows={6}
          readOnly
          onClick={(e) => {
            const cursorPosition = e.target.selectionStart;
            const lines = e.target.value.substr(0, cursorPosition).split("\n");
            const lineNumber = lines.length - 1;
            setSelectedLine(lineNumber);
          }}
        />
      </div>
      <Button 
        variant="outlined" 
        type="button"
        style={{ marginTop: '8px', alignSelf: 'flex-end' }}  // Adjusted style here
        onClick={() => {
          if (selectedLine !== null) {
            const newChoices = [...choices];
            newChoices.splice(selectedLine, 1);
            setChoices(newChoices);
            setSelectedLine(null);
          }
        }}
      >
        Delete Selected Choice
      </Button>
    </div>
  );
}

export default ChoicesArea;
