import React, { useRef, useEffect } from 'react';
import Button from '@mui/material/Button';

function ChoicesArea({ choices, setChoices, selectedLine, setSelectedLine }) {
  const textAreaRef = useRef(null);
  const shadowRef = useRef(null);

  useEffect(() => {
    if (selectedLine !== null) {
      const lineHeight = 20; // 根据你的textarea设置
      shadowRef.current.style.top = `${lineHeight * selectedLine}px`;
      shadowRef.current.style.display = 'block';
    } else {
      shadowRef.current.style.display = 'none';
    }
  }, [selectedLine]);

  return (
    <div style={{ position: 'relative', flex: 2, marginRight: '30px', marginLeft: '68px', marginTop: '20px' }}>
      <div ref={shadowRef} style={{ display: 'none', position: 'absolute', width: '100%', height: '20px', background: 'rgba(0,0,0,0.1)' }}></div>
      <textarea
        ref={textAreaRef}
        style={{ width: '100%', height: '200px', caretColor: 'transparent' }}
        value={choices.map((each) => each.value).join("\n")}
        rows={10}
        readOnly
        onClick={(e) => {
          const cursorPosition = e.target.selectionStart;
          const lines = e.target.value.substr(0, cursorPosition).split("\n");
          const lineNumber = lines.length - 1;
          setSelectedLine(lineNumber);
        }}
      />
      {/* your other code */}
    </div>
  );
}
