import React from 'react';
import Button from '@mui/material/Button';
import CircularProgress from '@mui/material/CircularProgress';

const LoadingButton = ({ isLoading, children, ...otherProps }) => {

  return (
    <Button
      variant="contained"
      type="submit"
      disabled={isLoading} //如果 isLoading 为 true，按钮会被禁用。
      {...otherProps}
    >
      {isLoading ? <CircularProgress size={24} /> : children}//如果 isLoading 为 true，则显示一个大小为 24 的 CircularProgress 组件。否则，显示 children（即按钮原本的内容）。
    </Button>
  );

};

export default LoadingButton;



