import React from 'react';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import MenuItem from '@mui/material/MenuItem';
import Checkbox from '@mui/material/Checkbox';
import FormControlLabel from '@mui/material/FormControlLabel';
import './TypeSelect.css'; // Import the CSS

function TypeSelect({ type, setType, isRequired, setIsRequired }) {
  return (
    <div className="type-select-container">
      <span className="type-label">Type</span>
      <FormControl className="form-control">
        <Select
          id="type-select"
          value={type}
          onChange={(e) => setType(e.target.value)}
        >
          <MenuItem value="Multi-select">Multi-select</MenuItem>
          <MenuItem value="Single-select">Single-select</MenuItem>
        </Select>
      </FormControl>
      <FormControlLabel
        className="form-control-label"
        control={
          <Checkbox
            checked={isRequired}
            onChange={() => setIsRequired(!isRequired)}
          />
        }
        label="A value is required"
      />
    </div>
  );
}

export default TypeSelect;
