import React, { useEffect, useState } from 'react';
import Button from '@mui/material/Button';

function ChoicesArea({ choices, setChoices, selectedLine, setSelectedLine }) {
  const [highlightedText, setHighlightedText] = useState(''); // 新增 state 用于存储高亮显示的文本

  useEffect(() => {
    if (selectedLine !== null) {
      const lines = choices.map((each) => each.value).join("\n").split("\n");
      const newHighlightedText = lines.map((line, index) => index === selectedLine ? `<mark>${line}</mark>` : line).join("\n"); // 用 <mark> 标签标记选中的行
      setHighlightedText(newHighlightedText);
    }
  }, [selectedLine, choices]);

  return (
    <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', marginLeft: '6%', marginRight: '20%', marginTop: '20px' }}>
      <label style={{ flex: 1 }}>Choices</label>
      <textarea
        style={{
          flex: 2,
          marginRight: '30px',
          marginLeft: '68px',
          marginTop: '20px',
          caretColor: 'transparent'  // 隐藏光标
        }}
        value={choices.map((each) => each.value).join("\n")}
        rows={10}
        readOnly
        onClick={(e) => {
          const cursorPosition = e.target.selectionStart;
          const lines = e.target.value.substr(0, cursorPosition).split("\n");
          const lineNumber = lines.length - 1;
          setSelectedLine(lineNumber);
        }}
        dangerouslySetInnerHTML={{ __html: highlightedText }}  // 使用 HTML 高亮显示选中的行
      />
      <Button 
        variant="outlined" 
        type="button"
        onClick={() => {
          if (selectedLine !== null) {
            const newChoices = [...choices];
            newChoices.splice(selectedLine, 1);
            setChoices(newChoices);
            setSelectedLine(null);
          }
        }}>
        Delete
      </Button>
    </div>
  );
}

export default ChoicesArea;
