import React from 'react';
import Button from '@mui/material/Button';
import './ChoicesArea.css'; // Import the CSS file

function ChoicesArea({ choices, setChoices, selectedLine, setSelectedLine }) {
  return (
    <div className="choices-area-container">
      <label className="choices-label">Choices</label>
      {/* Changed textarea to div and added spans for each choice */}
      <div
        className={`choices-textarea ${selectedLine !== null ? 'choices-textarea-selected' : ''}`}
        onClick={(e) => {
          const cursorPosition = e.target.selectionStart;
          const lines = e.target.textContent.substr(0, cursorPosition).split("\n");
          const lineNumber = lines.length - 1;
          setSelectedLine(lineNumber);
        }}
      >
        {choices.map((choice, index) => (
          <>
            <span key={index}>
              {choice.value.length <= 40 ? (
                choice.value
              ) : (
                <>
                  {choice.value.substring(0, 40)}
                  <span className="exceeding-characters">
                    {choice.value.substring(40)}
                  </span>
                </>
              )}
            </span>
            <br /> {/* Line break for each choice */}
          </>
        ))}
      </div>

      <Button
        className="choices-button"
        variant="outlined"
        type="button"
        onClick={() => {
          if (selectedLine !== null) {
            const newChoices = [...choices];
            newChoices.splice(selectedLine, 1);
            setChoices(newChoices);
            setSelectedLine(null);
          }
        }}
      >
        Delete
      </Button>
    </div>
  );
}

export default ChoicesArea;
